    if (telefoneLimpo.startsWith("55") && telefoneLimpo.length > 11) {
        telefoneLimpo = telefoneLimpo.slice(2);
    }

    let DDI = "55";
    let DDD = telefoneLimpo.slice(0, 2);
    let numero = telefoneLimpo.slice(2);

    if (DDD && numero) {
