const respostas = $json["custom_fields"] || [];
let arquivo = respostas.find(answer => answer.name === "Base64 da Mídia")?.value || "";
let tipo_msg = respostas.find(answer => answer.name === "Tipo de Mensagem")?.value || "";
let copy = $json["text_content"] || "";
let valor_msg = tipo_msg.toString().replace("0", "Texto").replace("1","Áudio").replace("2","Vídeo").replace("3","Imagem").replace("4","Documento")

return {
  arquivo,
  tipo_msg,
  copy,
  valor_msg
    }

