// Função para formatar a primeira letra de cada palavra em maiúsculo
function formatarPrimeiraLetraMaiuscula(str) {
  if (!str) {
    return ""; // Retorna uma string vazia se o campo estiver vazio
  }

  const palavras = str.toLowerCase().split(" ");

  for (let i = 0; i < palavras.length; i++) {
    palavras[i] = palavras[i].charAt(0).toUpperCase() + palavras[i].slice(1);
  }

  return palavras.join(" ");
}


