// Função para ajustar a hora
function ajustarHora(hora) {
    // Se a hora for indefinida, retorna uma string vazia
    if (!hora) return "";

    // Divide a hora em horas e minutos
    let [horas, minutos] = hora.split(':').map(Number);

    // Adiciona 3 horas
    horas += 3;

    // Se ultrapassar 24 horas, ajusta para o dia seguinte
    if (horas >= 24) {
        horas -= 24;
    }

    // Retorna a nova hora formatada
    return `${horas}:${minutos < 10 ? '0' : ''}${minutos}`;
}

