const dataAgendamento = new Date($node["pt_"].json["Data em que foi feito o Reagendamento"]);

const convertStringToDate = (dateString) => {
  const [dayMonthYear, time] = dateString.split(',');
  const [day, month, year] = dayMonthYear.trim().split('/');
  const [hour, minute] = time.trim().split(':');
  return new Date(year, month - 1, day, hour, minute);
};


const dataInicioString = $node["pt_"].json["Data de Início"];
const dataTerminoString = $node["pt_"].json["Data de Término"];

// Converter as strings de data para objetos Date
const dataInicio = convertStringToDate(dataInicioString);
const dataTermino = convertStringToDate(dataTerminoString);

// Adicionar 3 horas às datas
dataAgendamento.setHours(dataAgendamento.getHours() + 3);
dataInicio.setHours(dataInicio.getHours() + 3);
dataTermino.setHours(dataTermino.getHours() + 3);

// Converter as datas para o formato de string desejado (por exemplo, "YYYY-MM-DD HH:mm:ss")
const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');
  const seconds = String(date.getSeconds()).padStart(2, '0');

  return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
};

// Retornar as datas formatadas
return {
  "Data em que foi feito o Agendamento com Reagendamento": formatDate(dataAgendamento),
  "Data de Início com Adiantamento": formatDate(dataInicio),
  "Data de Término com Adiantamento": formatDate(dataTermino)
};
