const fullNameAnswer = $node["cadastro_isca_baleia"].json["body"]["Nome"] || "";
const emailAnswer = $node["cadastro_isca_baleia"].json["body"]["E-mail"] || "";
const telefoneClienteOriginal = $node["cadastro_isca_baleia"].json["body"]["WhatsApp com DDD"] || "";


function capitalizeName(fullName) {
    if (typeof fullName === 'undefined') {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    let words = fullName.toLowerCase().split(' ').map((word, index) => {
        if (lowerCaseWords.includes(word) && index !== 0) {
            return word;
        }
        return word.charAt(0).toUpperCase() + word.slice(1);
    });

    let fname = words.shift();
    let lname = words.join(' ');

    let fullname = lname ? `${fname} ${lname}` : fname;

    return { fname, lname, fullname };
}

function formatEmail(email) {
    return email.toLowerCase().trim();
}

function formatInstagram(instagram) {
    return instagram.toLowerCase().replace(/^@/, "");
}

function formatCPF(cpf) {
    const cpfLimpo = cpf.replace(/\D/g, "");
    if (cpfLimpo.length === 11) {
        return `${cpfLimpo.slice(0, 3)}.${cpfLimpo.slice(3, 6)}.${cpfLimpo.slice(6, 9)}-${cpfLimpo.slice(9, 11)}`;
    }
    return cpf;
}

function formatCNPJ(cnpj) {
    const cnpjLimpo = cnpj.replace(/\D/g, "");
    if (cnpjLimpo.length === 14) {
        return `${cnpjLimpo.slice(0, 2)}.${cnpjLimpo.slice(2, 5)}.${cnpjLimpo.slice(5, 8)}/${cnpjLimpo.slice(8, 12)}-${cnpjLimpo.slice(12, 14)}`;
    }
    return cnpj;
}

let telefoneFinal = "";

if (telefoneClienteOriginal) {
    const telefoneLimpo = telefoneClienteOriginal.replace(/\D/g, "");

    let DDI = '';
    let DDD = '';
    let numero = '';

    if (telefoneLimpo.length > 11) {
        DDI = telefoneLimpo.slice(0, 2);
        DDD = telefoneLimpo.slice(2, 4);
        numero = telefoneLimpo.slice(4);
    } else if (telefoneLimpo.length === 11) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else if (telefoneLimpo.length === 10) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else {
        telefoneFinal = telefoneLimpo;
    }

    if (DDD && numero) {
        if (parseInt(DDD) > 28) {
            if (numero.length === 9 && numero.startsWith("9")) {
                numero = numero.slice(1);
            }
        } else {
            if (numero.length === 8) {
                numero = `9${numero}`;
            }
        }
    }

    if (DDI && DDD && numero) {
        telefoneFinal = `${DDI}${DDD}${numero}`;
    }
}

const formattedNames = capitalizeName(fullNameAnswer);
const formattedEmail = formatEmail(emailAnswer);
const utm_source = $node["cadastro_isca_baleia"].json["body"]["utm_source"] || "";
const utm_campaign = $node["cadastro_isca_baleia"].json["body"]["utm_campaign"] || "";
const utm_medium = $node["cadastro_isca_baleia"].json["body"]["utm_medium"] || "";
const utm_term = $node["cadastro_isca_baleia"].json["body"]["utm_term"] || "";
const utm_content = $node["cadastro_isca_baleia"].json["body"]["utm_content"] || "";
const form_id = $node["cadastro_isca_baleia"].json["body"]["form_id"]  || "";
const form_name = $node["cadastro_isca_baleia"].json["body"]["form_name"] || "";


const result = {
    ...formattedNames,
    email: formattedEmail,
    whatsapp: telefoneFinal,
