            }
        }
    }

    if (DDI && DDD && numero) {
        telefoneFinal = `${DDI}${DDD}${numero}`;
    }
}

const formattedNames = capitalizeName(fullNameAnswer);
const formattedEmail = formatEmail(emailAnswer);

// UTM e infos do formulário
const utm_source = hidden["utm_source"] || "";
const utm_campaign = hidden["utm_campaign"] || "";
const utm_medium = hidden["utm_medium"] || "";
const utm_term = hidden["utm_term"] || "";
const utm_content = hidden["utm_content"] || "";
const form_id = $node["Isca-de-baleia"].json.body.form_response.form_id || "";
const form_name = $node["Isca-de-baleia"].json.body.form_response.definition?.title || "";

// Capturar respostas do Typeform
let quantos_vendedores = "";
let conhece_mentoria = "";
let faturamento_mensal = "";
let tem_socios = "";
let instagram_empresa = "";

answers.forEach(answer => {
    const fieldRef = answer.field.ref;

    switch (fieldRef) {
        case "fd1a27b7-fb73-40a8-bd42-a2d1c27a01ed": // Quantos vendedores?
            quantos_vendedores = answer.choice?.label || "";
            break;
        case "f9be3fc8-d97d-406b-b633-ef44898fff9a": // Já ouviu falar da mentoria?
            conhece_mentoria = answer.choice?.label || "";
            break;
        case "c2c91ede-d2c1-4245-bf78-589b779371a4": // Faturamento médio
            faturamento_mensal = answer.choice?.label || "";
            break;
        case "06045517-dab6-4de5-bc9e-ae1cdde3d0d3": // Tem sócios?
            tem_socios = answer.boolean === true ? "Sim" : "Não";
            break;
        case "bae8dbfc-3bc4-4b25-b431-4d32910b51eb": // Instagram
            instagram_empresa = formatInstagram(answer.text || "");
            break;
    }
});

// Montar objeto final
const result = {
    ...formattedNames,
    email: formattedEmail,
    whatsapp: telefoneFinal,
