// üîπ Capturar os dados do input
const inputData = items[0].json.body || {};

// üîß Utilidades
function formatEmail(email) {
  return email ? String(email).toLowerCase().trim() : "";
}

function processTelefone(telefoneOriginal) {
  if (!telefoneOriginal) return "";
  const digits = String(telefoneOriginal).replace(/\D/g, "");
  const withDDI = digits.startsWith("55") ? digits : `55${digits}`;
  const DDD = withDDI.slice(2, 4);
  let numero = withDDI.slice(4);
  if (numero.length === 8) numero = `9${numero}`;
  return `55${DDD}${numero}`;
}

function formatDateTimeGMT3(iso) {
  if (!iso) return "";
  const d = new Date(iso);
  d.setHours(d.getHours() - 3); // GMT-3 fixo
  const yyyy = d.getFullYear();
  const mm   = String(d.getMonth() + 1).padStart(2, "0");
  const dd   = String(d.getDate()).padStart(2, "0");
  const HH   = String(d.getHours()).padStart(2, "0");
  const II   = String(d.getMinutes()).padStart(2, "0");
  const SS   = String(d.getSeconds()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd} ${HH}:${II}:${SS}`;
}

// ‚ûï NOVA: formato BotConversa %d.%m.%Y %H:%M:%S (GMT-3)
function formatDateTimeBotConversa(iso) {
  if (!iso) return "";
  const d = new Date(iso);
  d.setHours(d.getHours() - 3); // GMT-3 fixo
  const dd = String(d.getDate()).padStart(2, "0");
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const yyyy = d.getFullYear();
  const HH = String(d.getHours()).padStart(2, "0");
  const II = String(d.getMinutes()).padStart(2, "0");
  const SS = String(d.getSeconds()).padStart(2, "0");
  return `${dd}.${mm}.${yyyy} ${HH}:${II}:${SS}`;
}

// üîß Fun√ß√£o para separar nome e sobrenome
function splitName(fullName) {
  if (!fullName) return { nome: "", sobrenome: "", nome_completo: "" };
  const parts = fullName.trim().split(/\s+/);
  const nome = parts.shift() || "";
  const sobrenome = parts.join(" ") || "";
  const nome_completo = [nome, sobrenome].filter(Boolean).join(" ");
  return { nome, sobrenome, nome_completo };
}

// üîé Extrair do payload
const nomeRaw     = inputData["name"] || "";
const email       = inputData["email"] || "";
const companyName = inputData["companyName"] || "";
const planName    = inputData["planName"] || "";
const planId      = inputData["planId"] || "";
const maxUsers    = inputData["maxUsers"] !== undefined ? String(inputData["maxUsers"]) : "";
const timestamp   = inputData["timestamp"] || "";
const TelefoneRaw = inputData["phone"] || "";
const cnpj        = inputData["cnpj"] || "";
const cpf         = inputData["cpf"] || "";
const form_id     = inputData["pageId"] || "";
const dataId      = inputData["dataId"] || "";
// UTMs
const utm_source   = inputData["utm_source"]   || "";
const utm_campaign = inputData["utm_campaign"] || "";
const utm_medium   = inputData["utm_medium"]   || "";
const utm_term     = inputData["utm_term"]     || "";
const utm_content  = inputData["utm_content"]  || "";

// üß± Montar o objeto final
const split = splitName(nomeRaw);

const result = {
  nome: split.nome,
  sobrenome: split.sobrenome,
  nome_completo: split.nome_completo,

  email: formatEmail(email),
  Telefone: processTelefone(TelefoneRaw),

  "Nome da empresa": companyName,
  "Tipo de plano":   planName,
  "ID do plano":     planId,
  "Usu√°rios":        maxUsers,    // string
  "CNPJ":            cnpj,
  "CPF":             cpf,

  "Data envio": formatDateTimeGMT3(timestamp),

  // ‚úÖ Novo campo no formato aceito pelo BotConversa
  data_botconversa: formatDateTimeBotConversa(timestamp),

  form_id: form_id,
  dataId: dataId,

  utm_source,
  utm_campaign,
  utm_medium,
  utm_term,
  utm_content
};

// ‚úÖ Retornar para o n8n
return [{ json: result }];

