// ===== FORMATA DADOS ‚Äî igual ao anterior, mas "data_garantia" agora √© DATE (yyyy-MM-dd) =====

const root = items[0]?.json ?? {};
const payload = Array.isArray(root) ? (root[0] ?? {}) : (root.body ?? root);

function capitalizeName(fullName) {
  if (!fullName) return { fname: '', lname: '', fullname: '' };
  const lowers = ['da','de','do','di','du'];
  const parts = String(fullName).toLowerCase().split(/\s+/).filter(Boolean)
    .map((w,i) => (lowers.includes(w) && i !== 0) ? w : w.charAt(0).toUpperCase() + w.slice(1));
  const fname = parts.shift() || '';
  const lname = parts.join(' ');
  return { fname, lname, fullname: `${fname}${lname ? ' ' + lname : ''}`.trim() };
}
function toZapiPhone(v){
  if (!v) return '';
  const raw = String(v).replace(/\D/g,'');
  const noDDI = raw.startsWith('55') ? raw.slice(2) : raw;
  const ddd = noDDI.slice(0,2);
  let num = noDDI.slice(2);
  if (num.length >= 9) num = num.slice(-9);
  else if (num.length === 8) num = num.slice(-8);
  return `55${ddd}${num}`;
}
function fmtEmail(e){ return e ? String(e).toLowerCase().trim() : ''; }

// ‚ûú Formata data/hora BR para TIMESTAMP ISO (com -3h)
function fmtDateBRToISO(dt) {
  if (!dt) return '';
  const s = String(dt).trim();
  let m = s.match(/^(\d{2})\/(\d{2})\/(\d{4})\s+(\d{2}):(\d{2})(?::(\d{2}))?$/);
  if (m) {
    const [, d, mo, y, h, mi, ss] = m;
    const date = new Date(`${y}-${mo}-${d}T${h}:${mi}:${ss ?? '00'}Z`);
    // aplica -3h
    date.setHours(date.getHours() - 0);
    const yyyy = date.getFullYear();
    const mm = String(date.getMonth() + 1).padStart(2, "0");
    const dd = String(date.getDate()).padStart(2, "0");
    const HH = String(date.getHours()).padStart(2, "0");
    const II = String(date.getMinutes()).padStart(2, "0");
    const SS = String(date.getSeconds()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd} ${HH}:${II}:${SS}`;
  }
  m = s.match(/^(\d{2})\/(\d{2})\/(\d{4})$/);
  if (m) {
    const [, d, mo, y] = m;
    return `${y}-${mo}-${d} 00:00:00`;
  }
  return '';
}

// ‚ûú Apenas DATE (yyyy-MM-dd)
function fmtDateBRToISODate(dt) {
  if (!dt) return '';
  const s = String(dt).trim();
  const m = s.match(/^(\d{2})\/(\d{2})\/(\d{4})(?:\s+\d{2}:\d{2}(?::\d{2})?)?$/);
  if (!m) return '';
  const [, d, mo, y] = m;
  return `${y}-${mo}-${d}`;
}
function mapStatus(s){ return s === 'waiting_payment' ? 'Aguardando Pagamento' : (s || ''); }
function numOrNull(v){ const n = Number(v); return Number.isFinite(n) ? n : null; }
function strOrEmpty(v){ return v == null ? '' : String(v); }

const nomeCliente   = payload['Nome do Cliente'] ?? '';
const emailCliente  = payload['E-mail do Cliente'] ?? '';
const telCliente    = payload['Telefone do Cliente'] ?? '';
const cpfcnpj       = payload['cpf/cnpj'] ?? '';
const nomeProduto   = payload['nome_produto'] ?? '';

const idTransacao   = payload['id_transacao_checkout'];
const idProduto     = payload['id_produto'];
const valor         = payload['valor'];
const oferta        = payload['oferta'] ?? '';
const formaPgto     = payload['forma_de_pagamento'] ?? '';
const statusCompra  = payload['status_da_compra'] ?? '';
const dataConvBR    = payload['data_conversao'] ?? '';
const period        = payload['period'];
const parcelas      = payload['parcelas'];
const ddd           = payload['ddd'] ?? '';
const cep           = payload['cep'];

const utm_source    = payload['utm_source'] ?? '';
const utm_campaign  = payload['utm_campaign'] ?? '';
const utm_medium    = payload['utm_medium'] ?? '';
const utm_content   = payload['utm_content'] ?? '';
const utm_term      = payload['utm_term'] ?? '';

const comissaoGw    = payload['comissao_gateway'];
const clientId      = payload['client_id'];
const sellerBalance = payload['seller_balance'];
const dataGarantiaBR= payload['data_garantia'] ?? '';
const codePix       = payload['code_pix'] ?? '';
const qrcodePix     = payload['qrcode_pix'] ?? '';
const moeda         = payload['moeda'] ?? '';
const linkPagamentoBoleto = payload['link_pagamento_boleto'] ?? '';

const { fname, lname, fullname } = capitalizeName(nomeCliente);
const emailFmt      = fmtEmail(emailCliente);
const whatsappZapi  = toZapiPhone(telCliente);
const dataConversao = fmtDateBRToISO(dataConvBR); // agora com -3h
const dataGarantia  = fmtDateBRToISODate(dataGarantiaBR); 
const statusFmt     = mapStatus(statusCompra);

const out = {
  fname, lname, fullname,
  email: emailFmt,
  whatsapp: whatsappZapi,

  "Nome do Cliente": fullname || strOrEmpty(nomeCliente),
  "E-mail do Cliente": emailFmt,
  "Telefone do Cliente": strOrEmpty(telCliente),
  "cpf/cnpj": strOrEmpty(cpfcnpj),
  "nome_produto": strOrEmpty(nomeProduto),

  "id_transacao_checkout": numOrNull(idTransacao),
  "id_produto": numOrNull(idProduto),
  "valor": numOrNull(valor),
  "oferta": strOrEmpty(oferta),
  "forma_de_pagamento": strOrEmpty(formaPgto),
  "status_da_compra": statusFmt,
  "data_conversao": dataConversao, // ‚úÖ ajustado com -3h
  "period": numOrNull(period),
  "parcelas": numOrNull(parcelas),
  "ddd": strOrEmpty(ddd),
  "cep": (cep === null ? null : strOrEmpty(cep)),

  "utm_source": strOrEmpty(utm_source),
  "utm_campaign": strOrEmpty(utm_campaign),
  "utm_medium": strOrEmpty(utm_medium),
  "utm_content": strOrEmpty(utm_content),
  "utm_term": strOrEmpty(utm_term),

  "comissao_gateway": numOrNull(comissaoGw),
  "client_id": numOrNull(clientId),
  "seller_balance": numOrNull(sellerBalance),
  "data_garantia": dataGarantia, 
  "code_pix": strOrEmpty(codePix),
  "qrcode_pix": strOrEmpty(qrcodePix),
  "moeda": strOrEmpty(moeda),
  "link_pagamento_boleto": strOrEmpty(linkPagamentoBoleto)
};

return [{ json: out }];

