// Capturar os dados do input
const inputData = items[0].json.body;

// Função para capitalizar o nome
function capitalizeName(fullName) {
    if (!fullName) {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    let words = fullName.toLowerCase().split(' ').map((word, index) => {
        if (lowerCaseWords.includes(word) && index !== 0) {
            return word;
        }
        return word.charAt(0).toUpperCase() + word.slice(1);
    });

    let fname = words.shift();
    let lname = words.join(' ');

    return { fname, lname, fullname: `${fname} ${lname}` };
}

// Função para formatar email
function formatEmail(email) {
    return email ? email.toLowerCase().trim() : "";
}

// Função para processar e formatar o telefone
function processTelefone(telefoneOriginal) {
    if (!telefoneOriginal) return "";
    const telefoneLimpo = telefoneOriginal.replace(/\D/g, ""); // Remove caracteres não numéricos
    const DDI = '55';
    const DDD = telefoneLimpo.slice(0, 2);
    let numero;

    if (parseInt(DDD) > 28) {
        numero = telefoneLimpo.slice(-8);
    } else {
        numero = telefoneLimpo.slice(-9);
    }

    return `${DDI}${DDD}${numero}`;
}

// Extrair e formatar os dados
const fullNameAnswer = inputData["Nome"] || "";
const emailAnswer = inputData["E-mail"] || "";
const telefoneClienteOriginal = inputData["Telefone"] || "";
const telefoneFinal = processTelefone(telefoneClienteOriginal);

const formattedNames = capitalizeName(fullNameAnswer);
const formattedEmail = formatEmail(emailAnswer);

// Capturar UTMs e outros campos
const utm_source = inputData["utm_source"] || "";
const utm_campaign = inputData["utm_campaign"] || "";
const utm_medium = inputData["utm_medium"] || "";
const utm_term = inputData["utm_term"] || "";
const utm_content = inputData["utm_content"] || "";
const form_id = inputData["form_id"] || "";
const form_name = inputData["form_name"] || "";
const data_envio = inputData["Data"] || "";
const horario_envio = inputData["Horário"] || "";
const url_pagina = inputData["URL da página"] || "";

// Montar o objeto de resultado
const result = {
    ...formattedNames, // Nome formatado
    email: formattedEmail, // Email formatado
    whatsapp: telefoneFinal, // WhatsApp formatado
    utm_source: utm_source,
    utm_campaign: utm_campaign,
    utm_term: utm_term,
    utm_medium: utm_medium,
    utm_content: utm_content,
    form_id: form_id,
    form_name: form_name,
    data_envio: data_envio,
    horario_envio: horario_envio,
    url_pagina: url_pagina
};

// Retorna o resultado como esperado pelo n8n
return [{ json: result }];

