// 📌 Capturar os dados de entrada
const inputData = items[0].json;
const formResponse = inputData.body.form_response;
const respostas = formResponse.answers || [];

// 📌 Mapear campos pelo ref correto
const perguntasMap = {
    "name": "fullName", // Nome completo
    "phone": "telefone", // WhatsApp com DDD
    "email": "email", // E-mail
    "07efbeb9-c1a5-4212-b99d-16c3db23c126": "qtd_vendedores", // Quantos vendedores
    "8331ae93-31f1-4960-b44d-abdb746e996d": "faturamento", // Faturamento
    "c709e7ca-83e2-48a9-a4e7-ce294107dc82": "desafio_comercial", // Desafio
    "5bd2513a-a94d-442a-badf-edd8600769f5": "endereço", // Endereço
    "15c73fe6-a626-4ed5-9dca-fa02f3101375": "pergunta_aberta", // Pergunta aberta
    "5e17bd16-ab60-45d7-9fa1-2bab5f7f2a76": "busca_diretor", // Busca Diretor
    "c699ad58-3a85-47bd-a05b-89d1e8401f54": "link_agendamento" // Calendly agendamento
};

// 📌 Criar objeto para armazenar as respostas
let respostasMapeadas = {};

// 📌 Iterar sobre as respostas para montar o objeto
respostas.forEach(answer => {
    const refPergunta = answer.field.ref;
    const chaveMapeada = perguntasMap[refPergunta] || refPergunta;
    let resposta = "";

    if (answer.type === "choice") {
        resposta = answer.choice.label || "";
    } else if (answer.type === "text" || answer.type === "email" || answer.type === "phone_number" || answer.type === "url") {
        resposta = answer[answer.type] || "";
    } else if (answer.type === "number") {
        resposta = answer.number;
    }

    respostasMapeadas[chaveMapeada] = resposta;
});

// ➕ Acrescentar nome/telefone/email/UTMs vindos de form_response.hidden (sem alterar o restante)
const hidden = (formResponse && formResponse.hidden) || {};
if (!respostasMapeadas.fullName)  respostasMapeadas.fullName  = hidden.name  || "";
if (!respostasMapeadas.telefone)  respostasMapeadas.telefone  = hidden.phone || "";
if (!respostasMapeadas.email)     respostasMapeadas.email     = hidden.email || "";

// 📌 Funções auxiliares
function capitalizeName(fullName) {
    if (!fullName) return { fname: "", lname: "", fullname: "" };

    const lowerCaseWords = ["da", "de", "do", "di", "du"];
    let words = fullName.toLowerCase().split(" ").map((word, index) =>
        lowerCaseWords.includes(word) && index !== 0 ? word : word.charAt(0).toUpperCase() + word.slice(1)
    );

    let fname = words.shift();
    let lname = words.join(" ");
    let fullname = lname ? `${fname} ${lname}` : fname;

    return { fname, lname, fullname };
}

function formatEmail(email) {
    return email ? email.toLowerCase().trim() : "";
}

function formatPhone(phone) {
    if (!phone) return "";
    const telefoneLimpo = phone.replace(/\D/g, "");
    let DDI = "55";
    let DDD = telefoneLimpo.slice(2, 4);
    let numero = telefoneLimpo.slice(4);

    if (numero.length === 8) {
        numero = `9${numero}`;
    }

    return `${DDI}${DDD}${numero}`;
}

function formatInstagram(instagram) {
    return instagram ? instagram.toLowerCase().replace(/^@/, "") : "";
}

// 📌 Organizar as informações
const formattedNames = capitalizeName(respostasMapeadas.fullName);
const formattedEmail = formatEmail(respostasMapeadas.email);
const telefoneFormatado = formatPhone(respostasMapeadas.telefone);
const instagramFormatado = formatInstagram(respostasMapeadas.instagram);

// 📌 Formatar data de aplicação
function formatDate(isoDate) {
    if (!isoDate) return "";
    const date = new Date(isoDate);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    const hours = String(date.getHours()).padStart(2, "0");
    const minutes = String(date.getMinutes()).padStart(2, "0");
    return `${year}-${month}-${day} ${hours}:${minutes}`;
}

const dataAplicacaoFormatada = formatDate(formResponse.submitted_at);

// 📌 Montar o resultado final (estrutura preservada) + UTMs do hidden
const result = {
    ...formattedNames,
    email: formattedEmail,
    whatsapp: telefoneFormatado,
    instagram: instagramFormatado,
    qtd_vendedores: respostasMapeadas.qtd_vendedores || "",
    faturamento: respostasMapeadas.faturamento || "",
    busca_diretor: respostasMapeadas.busca_diretor || "",
    desafio_comercial: respostasMapeadas.desafio_comercial || "",
    endereço: respostasMapeadas.endereço || "",
    pergunta_aberta: respostasMapeadas.pergunta_aberta || "",
    link_agendamento: respostasMapeadas.link_agendamento || "",
    form_id: formResponse.form_id || "",
    form_name: formResponse.definition.title || "",
    data_aplicacao: dataAplicacaoFormatada,

    // ➕ UTMs adicionadas aqui
    utm_source:  hidden.utm_source  || "",
    utm_medium:  hidden.utm_medium  || "",
    utm_campaign:hidden.utm_campaign|| "",
    utm_content: hidden.utm_content || "",
    utm_term:    hidden.utm_term    || ""
};

// 📌 Retornar para o n8n
return [{ json: result }];

