function capitalizeName(fullName) {
  if (!fullName) return { fname: '', lname: '', fullname: '' };
  const lowers = ['da','de','do','di','du'];
  const parts = String(fullName).toLowerCase().split(/\s+/).filter(Boolean)
    .map((w,i) => (lowers.includes(w) && i !== 0) ? w : w.charAt(0).toUpperCase() + w.slice(1));
  const fname = parts.shift() || '';
  const lname = parts.join(' ');
  return { fname, lname, fullname: `${fname}${lname ? ' ' + lname : ''}`.trim() };
}
function toZapiPhone(v){
  if (!v) return '';
  const raw = String(v).replace(/\D/g,'');
  const noDDI = raw.startsWith('55') ? raw.slice(2) : raw;
  const ddd = noDDI.slice(0,2);
  let num = noDDI.slice(2);
  if (num.length >= 9) num = num.slice(-9);
  else if (num.length === 8) num = num.slice(-8);
  return `55${ddd}${num}`;
}
function fmtEmail(e){ return e ? String(e).toLowerCase().trim() : ''; }
function fmtDateBRToISO(dt) {
  if (!dt) return '';
  const s = String(dt).trim();
  let m = s.match(/^(\d{2})\/(\d{2})\/(\d{4})\s+(\d{2}):(\d{2})(?::(\d{2}))?$/);
  if (m) {
    const [, d, mo, y, h, mi, ss] = m;
    return `${y}-${mo}-${d} ${h}:${mi}:${ss ?? '00'}`;
  }
  m = s.match(/^(\d{2})\/(\d{2})\/(\d{4})$/);
  if (m) {
    const [, d, mo, y] = m;
    return `${y}-${mo}-${d} 00:00:00`;
  }
  return '';
}
// ‚ûú NOVA: formata apenas para DATE (yyyy-MM-dd)
function fmtDateBRToISODate(dt) {
  if (!dt) return '';
  const s = String(dt).trim();
