// 📌 Capturar os dados de entrada (suporta payload como array ou objeto)
const root = Array.isArray(items[0].json) ? items[0].json[0] : items[0].json;
const formResponse = root?.body?.form_response || {};
const respostas = formResponse.answers || [];
const hidden = formResponse.hidden || {};

// 📌 Mapear SOMENTE os campos solicitados (conforme payload atual)
const perguntasMap = {
  "c1c7559d-1861-4414-8d09-66d3bf5539a5": "fullName",        // Qual seu nome?
  "5ee722e4-bb40-4a2a-a75c-7bb3d9182569": "nome_empresa",     // Nome da empresa
  "f091f638-6887-4bab-bb94-ee09bc6c90f0": "email",            // E-mail principal
  "9d2550a1-7fc2-4352-93fa-b64a8c6175cf": "telefone",         // Telefone da empresa
  "fd1a27b7-fb73-40a8-bd42-a2d1c27a01ed": "qtd_vendedores",   // Quantos vendedores?
  "f9be3fc8-d97d-406b-b633-ef44898fff9a": "conhece_mentoria", // Já ouviu falar da mentoria?
  "bae8dbfc-3bc4-4b25-b431-4d32910b51eb": "instagram",        // Instagram da empresa
  "06045517-dab6-4de5-bc9e-ae1cdde3d0d3": "possui_socio"      // Sócio/decisor junto? (yes_no)
};

// 🔤 Normalizador "sim"/"não"
function normalizeSimNao(v) {
  if (v === undefined || v === null) return "";
  const s = String(v).trim().toLowerCase();
  if (["sim","yes","y","true","1","on"].includes(s)) return "sim";
  if (["não","nao","no","n","false","0","off"].includes(s)) return "não";
  if (s.includes("sim")) return "sim";
  if (s.includes("não") || s.includes("nao")) return "não";
  return "";
}

// 🧠 Helpers de extração
function toLabel(answer) {
  if (!answer) return "";
  if (answer.type === "choice")  return answer.choice?.label || "";
  if (answer.type === "choices") return (answer.choices?.labels || []).join(", ");
  if (answer.type === "boolean") return String(!!answer.boolean);
  if (["text","email","phone_number","url","number"].includes(answer.type)) {
    return answer[answer.type] ?? "";
  }
  const raw = answer[answer.type] ?? answer.choice?.label ?? (answer.choices?.labels || []).join(", ") ?? "";
  return raw || "";
}

function extractPossuiSocio(answer) {
  if (!answer) return "";
  if (answer.type === "boolean") return normalizeSimNao(answer.boolean);
  if (answer.type === "choice")  return normalizeSimNao(answer.choice?.label);
  if (answer.type === "choices") {
    const labels = answer.choices?.labels || [];
    if (labels.some(l => normalizeSimNao(l) === "sim")) return "sim";
    if (labels.some(l => normalizeSimNao(l) === "não")) return "não";
    return normalizeSimNao(labels.join(", "));
  }
  const raw = toLabel(answer);
  return normalizeSimNao(raw);
}

function extractConheceMentoria(answer) {
  const label = toLabel(answer); // “Sim”/“Não” (multiple_choice)
  return normalizeSimNao(label);
}

function formatPhone(phone) {
  if (!phone) return "";
  const digitsOnly = String(phone).replace(/\D/g, "");
  const withDDI = digitsOnly.startsWith("55") ? digitsOnly : `55${digitsOnly}`;
  const DDD = withDDI.slice(2, 4);
  let numero = withDDI.slice(4);
  if (numero.length === 8) numero = `9${numero}`;
  return `55${DDD}${numero}`;
}

function formatInstagram(instagram) {
  return instagram ? String(instagram).toLowerCase().replace(/^@/, "") : "";
}

// 🧩 Separar nome em fname/lname
function splitName(name) {
  const s = String(name || "").trim().replace(/\s+/g, " ");
  if (!s) return { fname: "", lname: "" };
  const parts = s.split(" ");
  if (parts.length === 1) return { fname: parts[0], lname: "" };
  const lname = parts.slice(-1)[0];
  const fname = parts.slice(0, -1).join(" ");
  return { fname, lname };
}

// 📆 Converter submitted_at → YYYY-MM-DD HH:mm:ss GMT-3
function formatDateTimeGMT3(iso) {
  if (!iso) return "";
  const d = new Date(iso);
  d.setHours(d.getHours() - 3); // Ajuste -3h (SP sem DST)
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  const HH = String(d.getHours()).padStart(2, "0");
  const II = String(d.getMinutes()).padStart(2, "0");
  const SS = String(d.getSeconds()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd} ${HH}:${II}:${SS}`;
}

// 📦 Montagem apenas dos campos solicitados
const respostasMapeadas = {};
respostas.forEach(answer => {
  const ref = answer.field?.ref;
  const key = perguntasMap[ref];
  if (!key) return;

  if (key === "possui_socio") {
    respostasMapeadas.possui_socio = extractPossuiSocio(answer);
    return;
  }
  if (key === "conhece_mentoria") {
    respostasMapeadas.conhece_mentoria = extractConheceMentoria(answer);
    return;
  }

  if (answer.type === "choice") {
    respostasMapeadas[key] = answer.choice?.label || "";
  } else if (answer.type === "choices") {
    respostasMapeadas[key] = (answer.choices?.labels || []).join(", ");
  } else if (["text","email","phone_number","url"].includes(answer.type)) {
    respostasMapeadas[key] = answer[answer.type] || "";
  } else if (answer.type === "number") {
    respostasMapeadas[key] = answer.number ?? "";
  } else if (answer.type === "boolean") {
    respostasMapeadas[key] = String(!!answer.boolean);
  } else {
    respostasMapeadas[key] = "";
  }
});

// ➕ Fallbacks do hidden (considera nomes antigos e novos)
if (!respostasMapeadas.fullName) respostasMapeadas.fullName = hidden.name || hidden.first_name || "";
if (!respostasMapeadas.email)    respostasMapeadas.email    = hidden.email || "";
if (!respostasMapeadas.telefone) respostasMapeadas.telefone = hidden.phone || hidden.phone_number || "";
if (!respostasMapeadas.conhece_mentoria && hidden.conhece_mentoria) {
  respostasMapeadas.conhece_mentoria = normalizeSimNao(hidden.conhece_mentoria);
}
if (!respostasMapeadas.possui_socio && (hidden.possui_socio || hidden.comunicou_dono)) {
  respostasMapeadas.possui_socio = normalizeSimNao(hidden.possui_socio ?? hidden.comunicou_dono);
}

// Normalizações finais
if (respostasMapeadas.telefone) {
  respostasMapeadas.telefone = formatPhone(respostasMapeadas.telefone);
}
if (respostasMapeadas.instagram) {
  respostasMapeadas.instagram = formatInstagram(respostasMapeadas.instagram);
}

// 👤 Derivar fname, lname e fullname
const baseName = respostasMapeadas.fullName || hidden.name || hidden.first_name || "";
const { fname, lname } = splitName(baseName);
const fullname = respostasMapeadas.fullName || [fname, lname].filter(Boolean).join(lname ? " " : "");

// 📌 Capturar UTMs e outros campos
const utm_source   = hidden.utm_source   ?? root.utm_source   ?? "";
const utm_campaign = hidden.utm_campaign ?? root.utm_campaign ?? "";
const utm_medium   = hidden.utm_medium   ?? root.utm_medium   ?? "";
const utm_term     = hidden.utm_term     ?? root.utm_term     ?? "";
const utm_content  = hidden.utm_content  ?? root.utm_content  ?? "";

const form_id   = formResponse.form_id || "";
const form_name = formResponse.definition?.title || "";
const data_envio = formatDateTimeGMT3(formResponse.submitted_at);
const url_pagina = hidden.origin ?? hidden.url ?? root.origin ?? "";

// ✅ Resultado final
const result = {
  // Nome (mantém compat e adiciona breakdown)
  fullName:         respostasMapeadas.fullName        || "",
  fname,
  lname,
  fullname,

  nome_empresa:     respostasMapeadas.nome_empresa    || "",
  email:            respostasMapeadas.email           || "",
  telefone:         respostasMapeadas.telefone        || "",
  qtd_vendedores:   respostasMapeadas.qtd_vendedores  || "",
  conhece_mentoria: respostasMapeadas.conhece_mentoria|| "",
  instagram:        respostasMapeadas.instagram       || "",
  possui_socio:     respostasMapeadas.possui_socio    || "",

  utm_source,
  utm_campaign,
  utm_term,
  utm_medium,
  utm_content,
  form_id,
  form_name,
  data_envio,   // YYYY-MM-DD HH:mm:ss GMT-3
  url_pagina
};

return [{ json: result }];

