const horaInicio = $node["horarios_formatados"].json["HORA INICIO"];
const agora = $node["horarios_formatados"].json["AGORA"];

// Divida a hora de início e a hora atual em horas e minutos
const [horaInicioHora, horaInicioMinuto] = horaInicio.split(':').map(Number);
const [agoraHora, agoraMinuto] = agora.split(':').map(Number);

// Calcule a diferença em minutos
const diferencaMinutos = (agoraHora - horaInicioHora) * 60 + agoraMinuto - horaInicioMinuto;

// Crie um objeto com os resultados
const resultado = {
  "HORA INICIO": horaInicio,
  "AGORA": agora,
  "Diferença em Minutos": diferencaMinutos,
  "No Intervalo": diferencaMinutos >= -70 && diferencaMinutos <= -50
};

return resultado;

