function randomizar() {
    const currentDate = new Date();
    const currentHour = currentDate.getHours();
    const currentMinute = currentDate.getMinutes();

    // Calcula o número total de minutos desde meia-noite
    const totalMinutes = currentHour * 60 + currentMinute;

    // Divide o número total de minutos por 10 e verifica se é par ou ímpar
    const isEven = Math.floor(totalMinutes / 10) % 2 === 0;

    // Retorna 0 se for par, 1 se for ímpar
    const resultado = isEven ? 0 : 1;

    return [{ value: resultado }];
}

// Exemplo de uso
return randomizar();

// ============================================
// VALIDADOR DE DADOS - Detecta problemas ANTES do processamento
// ============================================

// Pega o primeiro item da fila
const content = $input.first().json.content;

// Array para armazenar erros encontrados
const errors = [];

// ============================================
// VALIDAÇÃO 1 - Email
// ============================================
// Por quê? API precisa de email válido para criar/atualizar contato
const email = content['E-mail do Cliente'];

if (!email) {
  errors.push('Email ausente');
} else if (!email.includes('@')) {
  errors.push('Email sem @');
} else if (!email.includes('.')) {
  errors.push('Email sem domínio');
} else if (email.trim() === '') {
  errors.push('Email vazio');
}

// ============================================
// VALIDAÇÃO 2 - Telefone
// ============================================
// Por quê? Telefone inválido causa erro na API
const telefone = content['Telefone do Cliente - Formato Z-API'];

if (!telefone) {
  errors.push('Telefone ausente');
} else if (telefone.length < 10) {
  errors.push('Telefone muito curto (mínimo 10 dígitos)');
} else if (!/^\d+$/.test(telefone)) {
  errors.push('Telefone contém caracteres não numéricos');
}

// ============================================
// VALIDAÇÃO 3 - Nome
// ============================================
// Por quê? Nome é obrigatório para criar contato
const nome = content['Nome do Cliente'];

if (!nome) {
  errors.push('Nome ausente');
} else if (nome.trim() === '') {
  errors.push('Nome vazio');
} else if (nome.length < 2) {
  errors.push('Nome muito curto');
}

// ============================================
// RESULTADO DA VALIDAÇÃO
// ============================================
if (errors.length > 0) {
  // DADOS INVÁLIDOS - Marcar para descarte imediato
  console.log('⚠️ VALIDAÇÃO FALHOU - Dados inválidos detectados');
  console.log('Erros encontrados:', errors.join(', '));
  console.log('Lead:', nome);
  console.log('Email:', email);
  console.log('Telefone:', telefone);
  console.log('---');

  // Adiciona flag para o IF usar
  $input.first().json.validacao_falhou = true;
  $input.first().json.motivo_falha = errors.join(', ');

} else {
  // DADOS VÁLIDOS - Pode processar normalmente
  console.log('✅ VALIDAÇÃO OK - Dados válidos');
  console.log('Lead:', nome);

  $input.first().json.validacao_falhou = false;
}

// Retorna o item modificado
return [$input.first()];

// ============================================
// 📊 LOG PROFISSIONAL - Mensagem Descartada
// ============================================
// Propósito: Registrar informações completas ANTES de descartar
// Permite: Auditoria, debug, análise e recuperação de dados

// ============================================
// SEÇÃO 1: COLETA DE DADOS
// ============================================

// Pega o item atual que chegou da fila
const data = $input.first().json;

// Extrai o conteúdo (dados do lead)
const content = data.content;

// Pega metadados do RabbitMQ
const deliveryCount = data.properties?.headers?.['x-delivery-count'] || 0;

// Pega resultado da validação (do node anterior)
const validacaoFalhou = data.validacao_falhou || false;
const motivoFalha = data.motivo_falha || 'Excedeu limite de tentativas';

// ============================================
// SEÇÃO 2: TIMESTAMP PRECISO
// ============================================
// Por quê? Para saber QUANDO foi descartado

const agora = new Date();

// Formato ISO (universal, para APIs e bancos)
const timestamp = agora.toISOString();
// Exemplo: "2025-10-14T16:45:30.123Z"

// Formato BR (humano-legível, para relatórios)
const dataFormatada = agora.toLocaleString('pt-BR', {
  timeZone: 'America/Sao_Paulo'
});
// Exemplo: "14/10/2025, 13:45:30"

// ============================================
// SEÇÃO 3: LOG ESTRUTURADO E VISUAL
// ============================================
// Por quê caracteres especiais? Destacar nos logs!

console.log('╔════════════════════════════════════════════╗');
console.log('║   🚨 MENSAGEM DESCARTADA - LOG COMPLETO    ║');
console.log('╚════════════════════════════════════════════╝');
console.log('');

// Timestamps
console.log('📅 Timestamp (ISO):', timestamp);
console.log('🕐 Horário Brasil:', dataFormatada);
console.log('');

// Separador visual
console.log('━━━ INFORMAÇÕES DO LEAD ━━━');

// Dados principais do lead
console.log('👤 Nome:', content['Nome do Cliente'] || '(não informado)');
console.log('📧 Email:', content['E-mail do Cliente'] || '(não informado)');
console.log('📱 Telefone:', content['Telefone do Cliente - Formato Z-API'] || '(não informado)');
console.log('🆔 ID Lead BD:', content.id_lead_bd || '(não informado)');
console.log('🆔 ID Lead BC:', content.id_lead_bc || '(não informado)');
console.log('');

// Informações do funil
console.log('━━━ INFORMAÇÕES DO FUNIL ━━━');
console.log('🎯 Funil:', content.funil || '(não informado)');
console.log('🔄 Variação:', content.variacao_do_funil || '(não informado)');
console.log('');

// Motivo do descarte (MAIS IMPORTANTE!)
console.log('━━━ MOTIVO DO DESCARTE ━━━');
console.log('🔢 Tentativas:', deliveryCount);
console.log('❌ Validação falhou?', validacaoFalhou ? 'SIM' : 'NÃO');
console.log('📝 Motivo específico:', motivoFalha);
console.log('');

// UTM para análise de origem
console.log('━━━ UTM TRACKING ━━━');
console.log('Source:', content.utm_source || '(não informado)');
console.log('Campaign:', content.utm_campaign || '(não informado)');
console.log('Medium:', content.utm_medium || '(não informado)');
console.log('Term:', content.utm_term || '(não informado)');
console.log('Content:', content.utm_content || '(não informado)');
console.log('');

// Payload completo em JSON (para debug técnico)
console.log('━━━ PAYLOAD COMPLETO (JSON) ━━━');
console.log(JSON.stringify(content, null, 2));
console.log('');

// Separador final
console.log('═══════════════════════════════════════════');
console.log('');

// ============================================
// SEÇÃO 4: ADICIONAR METADATA AO PAYLOAD
// ============================================
// Por quê? Para usar em nodes seguintes (webhook, DLQ, etc)

$input.first().json.metadata_descarte = {
  timestamp: timestamp,
  timestamp_br: dataFormatada,
  tentativas: deliveryCount,
  validacao_falhou: validacaoFalhou,
  motivo: motivoFalha,
  nome_lead: content['Nome do Cliente'],
  email_lead: content['E-mail do Cliente'],
  telefone_lead: content['Telefone do Cliente - Formato Z-API']
};

// ============================================
// SEÇÃO 5: RETORNAR DADOS
// ============================================
// Passa o item para o próximo node (Stop and Error)

return [$input.first()];
