// Função para formatar o telefone com DDI, DDD e número
function formatarTelefone(telefone) {
  let telefoneLimpo = telefone.replace(/[^\d]/g, ''); // Remover caracteres não numéricos (espaços, traços, etc.)

  // Remover "55" do início do telefone, se presente
  if (telefoneLimpo.startsWith('55')) {
    telefoneLimpo = telefoneLimpo.slice(2);
  }

  let ddi = '55';
  let ddd = telefoneLimpo.slice(0, 2);
  let numeroSemNono = telefoneLimpo.slice(-8);
  let numeroComNono = telefoneLimpo.slice(-9);

  // Verifica se o DDD é maior que 28 e ajusta o número
  if (parseInt(ddd) > 28) {
    return ddi + ddd + numeroSemNono;
  } else {
    return ddi + ddd + numeroComNono;
  }
}

// Função para validar se o parâmetro "email" é um email válido e colocar todas as letras em minúsculo
function validarEmail(email) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}


function formatarCPForCNPJ(valor) {
  if (!valor) return ""; // Retorna string vazia se o valor for vazio

  const numeros = valor.replace(/\D/g, ''); // Remove caracteres não numéricos

  if (numeros.length === 11) { // Formatação de CPF
    return numeros.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4");
  } else if (numeros.length === 14) { // Formatação de CNPJ
    return numeros.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, "$1.$2.$3/$4-$5");
  } else {
    return valor; // Retorna o valor original se não for um CPF ou CNPJ válido
  }
}
// Função para formatação de nomes
function formatarNome(nome) {
  if (nome) {
    const palavras = nome.trim().split(/\s+/);

    const palavrasMinusculas = ['de', 'dos', 'da', 'e', 'do', 'das', 'di', 'du', 'o', 'a', 'u'];

    for (let i = 0; i < palavras.length; i++) {
      const palavraAtual = palavras[i].toLowerCase();
      if (i === 0 || i === palavras.length - 1 || !palavrasMinusculas.includes(palavraAtual)) {
        palavras[i] = palavraAtual.charAt(0).toUpperCase() + palavraAtual.slice(1);
      } else {
        palavras[i] = palavraAtual;
      }
    }

    return palavras.join(" ");
  }
  return '';
}
// Dados de entrada através das variáveis (um único cliente ou uma matriz de clientes)
const clientes = Array.isArray($node["consulta_cliente"].json) ? $node["consulta_cliente"].json : [$node["consulta_cliente"].json];
const documento = formatarCPForCNPJ($node["consulta_cliente"].json["document"]["number"]) || '';
// Processar cada cliente individualmente
const output = clientes.map(cliente => {
  const { name: NomedoCliente, communication } = cliente;
  const { email, cellPhone: telefone } = communication;

  // Verificar se o campo "cellPhone" está vazio e utilizar o campo "contactName" como backup
  let telefoneFormatado = null;
  if (telefone) {
    telefoneFormatado = formatarTelefone(telefone);
  } else if (communication["contactName"]) {
    telefoneFormatado = formatarTelefone(communication["contactName"]);
  }

  // Validar se o parâmetro "email" é um email válido e colocar todas as letras em minúsculo
  const emailValido = validarEmail(email);

  return {
    Nome: formatarNome(NomedoCliente),
    telefone: telefoneFormatado,
    email: emailValido ? email.toLowerCase() : null,
    cpf_cnpj: documento
  };
});

return output;

