const formatAddress = (address) => {
    let formattedAddress = '';

    if (address && address.street) {
        formattedAddress += address.street;
    }

    if (address && address.number) {
        formattedAddress += `, ${address.number}`;
    }

    if (address && address.complement) {
        formattedAddress += ` - ${address.complement}`;
    }

    if (address && address.zip_code) {
        formattedAddress += `, ${address.zip_code}`;
    }

    if (address && address.neighborhood) {
        formattedAddress += `, ${address.neighborhood}`;
    }

    if (address && address.city && address.city.name) {
        formattedAddress += `, ${address.city.name}`;
    }

    if (address && address.state && address.state.name) {
        formattedAddress += ` - ${address.state.name}`;
    }

    return formattedAddress.trim();
};

