let itens_coletados = $node["init_paginacao"].json["itens_coletados"] || [];
const itens_pagina = $node["busca_itens_totais"].json["itens"] || [];
const mensagem_tipo = $node["init_paginacao"].json["mensagem_tipo"];

// Adiciona mensagem_tipo a cada item para usar no template depois
const itens_formatados = itens_pagina.map(item => ({
  ...item,
  mensagem_tipo: mensagem_tipo
}));

itens_coletados = [...itens_coletados, ...itens_formatados];

return [{ json: { 
  pagina: $node["init_paginacao"].json["pagina"] + 1,  // Prepara para page 2
  itens_coletados,
  itens_totais: $node["init_paginacao"].json["itens_totais"]
} }];

const totalItens = $node["busca_itens_totais"].json["itens_totais"];
const pageSize = 50;

const totalPaginas = Math.ceil(totalItens / pageSize);

const paginas = [];
for (let i = 1; i <= totalPaginas; i++) {
  paginas.push({ pagina: i });
}

return paginas;

const hoje = $now.toISODate().split('T')[0];
const configs = [
  { nome: '5 dias antes', data_de: $now.plus({ days: 5 }).toISODate().split('T')[0], data_ate: $now.plus({ days: 5 }).toISODate().split('T')[0], status: 'PENDING', mensagem_tipo: '5_antes' },
  { nome: '2 dias antes', data_de: $now.plus({ days: 2 }).toISODate().split('T')[0], data_ate: $now.plus({ days: 2 }).toISODate().split('T')[0], status: 'PENDING', mensagem_tipo: '2_antes' },
  { nome: '1 dia antes', data_de: $now.plus({ days: 1 }).toISODate().split('T')[0], data_ate: $now.plus({ days: 1 }).toISODate().split('T')[0], status: 'PENDING', mensagem_tipo: '1_antes' },
  { nome: 'No dia', data_de: hoje, data_ate: hoje, status: 'PENDING', mensagem_tipo: 'no_dia' },
