  { nome: '5 dias atraso', data_de: $now.minus({ days: 5 }).toISODate().split('T')[0], data_ate: $now.minus({ days: 5 }).toISODate().split('T')[0], status: 'ATRASADO', mensagem_tipo: '5_atraso' }
];
return configs.map(config => ({ json: config }));
