function formatarNome(nome) {
  if (nome) {
    const palavras = nome.trim().split(/\s+/);

    const palavrasMinusculas = ['de', 'dos', 'da', 'e', 'do', 'das', 'di', 'du', 'o', 'a', 'u'];

    for (let i = 0; i < palavras.length; i++) {
      const palavraAtual = palavras[i].toLowerCase();
      if (i === 0 || i === palavras.length - 1 || !palavrasMinusculas.includes(palavraAtual)) {
        palavras[i] = palavraAtual.charAt(0).toUpperCase() + palavraAtual.slice(1);
      } else {
        palavras[i] = palavraAtual;
      }
    }

    return palavras.join(" ");
  }
  return '';
}

function formatarCPFCNPJ(valor) {
  if (!valor) {
    return ""; // Retorna uma string vazia se o campo estiver vazio
  }

  valor = valor.toString().replace(/[^\d]/g, ''); // Certifica-se de que o valor seja uma string e remove caracteres n√£o num√©ricos

  if (valor.length === 11) {
    // Se o tamanho for 11, assume que √© CPF e formata como CPF
    return valor.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  } else if (valor.length === 14) {
    // Se o tamanho for 14, assume que √© CNPJ e formata como CNPJ
    return valor.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  } else {
    // Caso contr√°rio, retorna o valor original sem formata√ß√£o
    return valor;
  }
}

function formatarCEP(valor) {
  if (!valor) {
    return ""; // Retorna uma string vazia se o campo estiver vazio
  }

  valor = valor.toString().replace(/[^\d]/g, ''); // Certifica-se de que o valor seja uma string e remove caracteres n√£o num√©ricos

  if (valor.length === 8) {
    // Se o tamanho for 8, formata como CEP
    return valor.replace(/(\d{5})(\d{3})/, '$1-$2');
  } else {
    // Caso contr√°rio, retorna o valor original sem formata√ß√£o
    return valor;
  }
}

function formatarReal(valor) {
  if (typeof valor === 'string') {
    valor = parseFloat(valor.replace(',', '.')); // Converte string para n√∫mero, substituindo a v√≠rgula por ponto se necess√°rio
  }

  if (isNaN(valor)) {
    return ""; // Retorna uma string vazia se o valor n√£o for um n√∫mero v√°lido
  }

  return valor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });
}

