={{ 
  (() => {
    const telefone = $json["body"]["client"]["cellphone"];
    if (!telefone) {
      return '';
    }

    const telefoneLimpo = telefone.replace(/[^\d]/g, '');

    let ddi;
    let ddd;
    let telefoneFormatado;

    // Verificar se o telefone começa com "55" antes de formatá-lo
    if (telefoneLimpo.startsWith('55')) {
      ddi = telefoneLimpo.slice(0, 2);
      ddd = telefoneLimpo.slice(2, 4);
      if (parseInt(ddd, 10) > 28) {
        telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
      } else {
        telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
      }
    } else {
      // Se o DDI for diferente de 55, manter o número como está
      telefoneFormatado = telefoneLimpo;
    }

    return '+' + telefoneFormatado;
  })() 
}}

