// Atalho pros dados do webhook
const webhookData = $node["preencheu_pre_forms"].json || {};
const body = webhookData.body || {};
const headers = webhookData.headers || {};

// Formatar o n√∫mero de telefone
const telefone = body["WhatsApp com DDD"] || '';
const telefoneLimpo = telefone.replace(/[^\d]/g, '');

let ddi;
let ddd;
let telefoneFormatado;

// Obter a data atual
