// Subtrair 15 dias
dataAtual.setDate(dataAtual.getDate() - 15);

// Formatar a data para YYYY-MM-DD
const ano = dataAtual.getFullYear();
const mes = String(dataAtual.getMonth() + 1).padStart(2, '0'); // +1 porque os meses come√ßam em 0
const dia = String(dataAtual.getDate()).padStart(2, '0');

const dataFormatada = `${ano}-${mes}-${dia}`;

console.log(dataFormatada); // Sa√≠da no formato YYYY-MM-DD

// Verificar se o telefone come√ßa com "55" antes de format√°-lo
if (telefoneLimpo.startsWith('55')) {
  ddi = telefoneLimpo.slice(0, 2);
  ddd = telefoneLimpo.slice(2, 4);
  if (parseInt(ddd, 10) > 28) {
    telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
  } else {
    telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
  }
} else {
  ddi = '55';
  ddd = telefoneLimpo.slice(0, 2);
  if (parseInt(ddd, 10) > 28) {
    telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
  } else {
    telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
  }
}

// Pegar o email em min√∫sculas e sem espa√ßos no final
const email = (body["E-mail"] || "").toLowerCase().trim();

// Captar os par√¢metros UTM
const utm_source = body["utm_source"] || "";
const utm_campaign = body["utm_campaign"] || "";
const utm_term = body["utm_term"] || "";
const utm_medium = body["utm_medium"] || "";
const utm_content = body["utm_content"] || "";
const nome = body["Nome"] || "";

// Quebrar nome em primeiro nome e sobrenome (pra usar nos hashes)
let primeiroNome = "";
let sobrenome = "";
if (nome) {
  const partesNome = String(nome).trim().split(/\s+/);
  primeiroNome = partesNome[0] || "";
  sobrenome = partesNome.slice(1).join(" ") || "";
}

// Campos adicionais do payload
const fbclid = body["fbclid"] || "";
const full_url = body["full_url"] || "";
const cidade = body["Cidade"] || "";
const estado = body["Estado"] || "";
const cep = body["CEP"] || "";

// Pa√≠s ‚Äúnormal‚Äù ‚Äì se n√£o vier nada, considera BR
const pais = body["Pa√≠s"] || "Brasil";

// Hash Pa√≠s vem do campo espec√≠fico ou cai pro pa√≠s padr√£o
const hash_pais = body["Hash Pa√≠s"] || pais;

// FBC / FBP
const fbc = body["FBC"] || "";
const fbp = body["FBP"] || "";

// üëâ Agora preenchemos usando headers como fallback
const ip_usuario =
  body["IP do usu√°rio"] ||
  headers["x-real-ip"] ||
  headers["x-forwarded-for"] ||
  "";

const user_agent =
  body["User Agent"] ||
  headers["user-agent"] ||
  "";

// URL da p√°gina: prioriza campo espec√≠fico, sen√£o usa full_url
const url_pagina =
  body["URL da p√°gina"] ||
  full_url ||
  "";

// Hashes preenchidos com os dados comuns
const hash_primeiro_nome = body["Hash Primeiro Nome"] || primeiroNome || "";
const hash_sobrenome     = body["Hash Sobrenome"]     || sobrenome     || "";
const hash_email         = body["Hash Email"]         || email         || "";
const hash_telefone      = body["Hash Telefone"]      || telefoneFormatado || "";
const hash_cidade        = body["Hash Cidade"]        || cidade        || "";
const hash_estado        = body["Hash Estado"]        || estado        || "";
const hash_cep           = body["Hash CEP"]           || cep           || "";

const form_id = body["form_id"] || "";
const form_name = body["form_name"] || "";

// Criar um objeto com os valores formatados
const formattedData = {
  email,
  telefone: telefoneFormatado,
  utm_source,
  utm_campaign,
  utm_term,
  utm_medium,
  utm_content,
  nome,
  dataFormatada,

  fbclid,
  full_url,
  cidade,
  estado,
  cep,
  hash_pais,
  fbc,
  fbp,
  ip_usuario,
  user_agent,
  url_pagina,
  hash_primeiro_nome,
  hash_sobrenome,
  hash_email,
  hash_telefone,
  hash_cidade,
  hash_estado,
  hash_cep,
  form_id,
  form_name
};

// Retorna o objeto formatado como JSON
return formattedData;

