function getDataHoraPortugal() {
  const dataAtual = new Date();
  // Ajustar o fuso hor√°rio para Portugal (GMT+0)
  const fusoHorarioPortugal = 0;
  const horaAtualPortugal = new Date(dataAtual.getTime() + (fusoHorarioPortugal * 60 * 60 * 1000));
  return horaAtualPortugal;
}

